import { createContext } from "react";

interface IGoalContext {
  price: number;
  date: Date;
  monthlyAmount: number;
}

export const defaultState: IGoalContext = {
  price: 0,
  date: new Date(),
  monthlyAmount: 0,
};

function goalReducer(state = defaultState, action) {
  switch (action) {
    case "changePrice":
      return {
        ...state,
        price: action.payload,
      };
  }
}

export const GoalContext = createContext(defaultState);
