import React, { useContext } from "react";
import { GoalContext } from "../../GoalContext";
import { styles } from "./MonthlyAmount.styles";

const MonthlyAmount = (): JSX.Element => {
  const goal = useContext(GoalContext);

  return (
    <div style={styles.container}>
      <div style={styles.topSection}>
        <div>Monthly amount</div>
        <div style={styles.price}>$520.83</div>
      </div>
      <p style={styles.text}>
        You’re planning<strong>48 monthly deposits</strong> to reach your
        <strong>{goal.price}</strong> goal by <strong>October 2020.</strong>
      </p>
    </div>
  );
};

export default MonthlyAmount;
