import React from "react";
import "./styles.css";
import { Props, months } from "../../types";

const Inputs = (props: Partial<Props>): JSX.Element => {
  const { price, setPrice, date, setDate } = props;

  const handlePrice = (e: React.FormEvent<HTMLInputElement>) => {
    setPrice!(Number(e.currentTarget.value));
  };

  return (
    <div className="fields">
      <div className="priceSection">
        <h4 className="title">Total amount</h4>
        <div className="inputWrapper">
          <span className="currency">$</span>
          <input
            type="number"
            className="input"
            placeholder="price"
            // name="amount"
            // value={price}
            //onChange={(price) => handlePrice(price)}
          />
        </div>
      </div>

      <div className="dateSection">
        <h4 className="title">Reach goal by</h4>
        <div className="date">{months[new Date().getMonth()]}</div>
      </div>
    </div>
  );
};

export default Inputs;
