import React, { useState } from "react";
import DatePicker from "react-datepicker";
import "react-datepicker/dist/react-datepicker.css";
import CurrencyInput from "react-currency-input-field";
import { styles } from "./Inputs.styles";
import { Props } from "../../types";

const Inputs = (props: Partial<Props>): JSX.Element => {
  const { price, setPrice, date, setDate } = props;

  const handleChange = (e) => {
    let name = e.target.name;
    let value = e.target.value;
    name === "amount" ? setPrice!(value) : setDate!(value);
  };

  return (
    <div style={styles.inputs}>
      <div style={styles.price}>
        <h4 style={styles.title}>Total amount</h4>
        <input
          type="number"
          name="amount"
          pattern="^\$\d{1,3}(,\d{3})*(\.\d+)?$"
          value={`$ ${price}`}
          onChange={(e) => handleChange(e)}
          style={styles.input}
        />
        <CurrencyInput
          id="input-example"
          name="input-name"
          placeholder="Please enter a number"
          defaultValue={1000}
          decimalsLimit={2}
          onValueChange={(value, name) => console.log(value, name)}
        />
        ;
      </div>
      <div style={styles.date}>
        <h4 style={styles.title}>Reach goal by</h4>
        {/* <input
          type="date"
          placeholder="Date"
          style={styles.input}
          name="reachDate"
          value={date}
          onChange={(date) => setDate(date)}
        /> */}
        <DatePicker
          selected={date}
          onChange={(date: Date) => handleChange!(date)}
        />
      </div>
    </div>
  );
};

export default Inputs;
