import React from "react";
import { styles } from "./Inputs.styles";
import { Props, months } from "../../types";

const Inputs = (props: Partial<Props>): JSX.Element => {
  const { price, setPrice, date, setDate } = props;

  const handlePrice = (e: React.FormEvent<HTMLInputElement>) => {
    setPrice!(Number(e.currentTarget.value));
  };

  return (
    <div style={styles.fields}>
      <div style={styles.priceSection}>
        <h4 style={styles.title}>Total amount</h4>
        <div style={styles.inputWrapper}>
          <span style={styles.currency}>$</span>
          <input
            type="number"
            style={styles.input}
            placeholer="price"
            // name="amount"
            // value={price}
            //onChange={(price) => handlePrice(price)}
          />
        </div>
      </div>

      <div style={styles.dateSection}>
        <h4 style={styles.title}>Reach goal by</h4>
        <div style={styles.date}>{months[new Date().getMonth()]}</div>
      </div>
    </div>
  );
};

export default Inputs;
