import type { ReactNode } from "react";
import { createContext, useReducer, useContext } from "react";

// interface IGoalContext {
//   price: number;
//   date: Date;
//   monthlyAmount: number;
// }

export const defaultState = {
  price: 0,
  date: new Date(),
  monthlyAmount: 0,
};

export type Action = "changePrice" | "changeDate";
export type Dispatch = (action: Action) => void;
export type State = typeof defaultState;

function goalReducer(state: State, action: Action) {
  switch (action) {
    case "changePrice":
      return {
        ...state,
        price: action,
      };
    case "changeDate":
      return {
        ...state,
        date: action,
      };
  }
}

export const GoalContext = createContext(defaultState);

export function GoalProvider({ children }: { children: ReactNode }) {
  const [state, dispatch] = useReducer(goalReducer, defaultState);

  return (
    <GoalContext.Provider value={{ state: State, dispatch }}>
      {children}
    </GoalContext.Provider>
  );
}

//custom Hook
export function useGoal() {
  const context = useContext(GoalContext);
  if (!context) throw new Error("useGoal must be used inside a GoalContext");

  return context;
}
