import React, { useState } from "react";
import styles from "./Inputs.module.css";
import DatePicker from "react-datepicker";

import "react-datepicker/dist/react-datepicker.css";

interface Props {
  price: number;
  setPrice(price: number): void;
  date: Date;
  setDate(date: Date): void;
}

const Inputs = (props: Props): JSX.Element => {
  const [startDate, setStartDate] = useState(new Date());
  const { price, setPrice, date, setDate } = props;

  return (
    <div className={styles.fields}>
      <div className={styles.priceSection}>
        <h4 className={styles.title}>Total amount</h4>
        <div className={styles.inputWrapper}>
          <span className={styles.currency}>$</span>
          <input
            type="number"
            className={styles.input}
            placeholder="price"
            name="amount"
            onChange={(e) => setPrice(Number(e.target.value))}
          />
        </div>
      </div>

      <div className={styles.dateSection}>
        <h4 className={styles.title}>Reach goal by</h4>
      </div>
      <DatePicker selected={startDate} onChange={(e) => setStartDate(e)}>
        <div style={{ color: "red" }}>Don't forget to check the weather!</div>
      </DatePicker>
    </div>
  );
};

export default Inputs;
