import React from "react";
import { ToastContainer, toast } from "react-toastify";
import "react-toastify/dist/ReactToastify.css";
import styles from "./ConfirmBtn.module.css";

const ConfirmBtn: React.FC = () => {
  const handleConfirm = () => {
    if (price > 1 || date !== new Date()) {
      toast(
        `Your monthly amount set to ${price} and your total price goal set to ${monthlyAmount}`
      );
    } else {
      toast(
        "You should set your price/date to a different value than the defaults"
      );
    }
  };
  return (
    <button onClick={handleConfirm} className={styles.btn}>
      Confirm
    </button>
  );
};

export default ConfirmBtn;
