import React, { useContext } from "react";
import { styles } from "./Inputs.styles";
import { GoalContext } from "../../GoalContext";

const Inputs = (): JSX.Element => {
  const price = useContext(GoalContext);

  // const handleChange = (price: number) => {
  //   let newPrice = Intl.NumberFormat("en-US").format(price);
  //   setPrice(Number(newPrice));
  // };

  return (
    <div style={styles.inputs}>
      <div style={styles.field}>
        <h4 style={styles.title}>Total amount</h4>
        <input
          type="number"
          placeholder="Price"
          name="price"
          value={price}
          onChange={(price) => handleChange(price)}
          style={styles.input}
        />
      </div>
      <div style={styles.field}>
        <h4 style={styles.title}>Reach goal by</h4>
        <input
          type="text"
          placeholder="Date"
          style={styles.input}
          name="date"
          value={Date.now()}
        />
      </div>
    </div>
  );
};

export default Inputs;
