import React, { useEffect, useState } from "react";
import ConfirmBtn from "../ConfirmBtn";
import ContentHead from "../ContentHead/ContentHead";
import Inputs from "../Inputs";
import MonthlyAmount from "../MonthlyAmount";
import styles from "./Content.module.css";

const Content: React.FC = () => {
  const [price, setPrice] = useState<number>(0);
  const [date, setDate] = useState<Date>(new Date());
  const [monthlyAmount, setMonthlyAmount] = useState<number>(0);

  //mountly amount calc = price * (selected ms - date.now ms / 30 );
  useEffect(() => {
    // setMonthlyAmount(price * (date - new Date().toMonth()) / 30));
  }, [price, date]);

  return (
    <div className={styles.content__container}>
      <ContentHead />
      <Inputs
        price={price}
        setPrice={setPrice}
        date={date}
        setDate={setDate}
        setMonthlyAmount={setMonthlyAmount}
      />
      <MonthlyAmount monthlyAmount={monthlyAmount} price={price} />
      <ConfirmBtn />
    </div>
  );
};

export default Content;
