import React, { useState } from "react";
import DatePicker from "react-datepicker";

import "react-datepicker/dist/react-datepicker.css";

const MonthYearPicker = () => {
  const [month, setMonth] = useState<Date>(new Date());

  const handleChangeMonth = (date: Date) => {
    let selected_month_To_ms = date.getTime();
    let now_to_ms = new Date().getTime();
    //setMonth(date!);
    let days_to_goal = (selected_month_To_ms - now_to_ms) % 86400000;
    // let months_to_goal = ;
    console.log(selected_month_To_ms, now_to_ms);
  };
  return (
    <DatePicker
      dateFormat="MMMM yyyy"
      showMonthYearPicker
      selected={month}
      onChange={(date) => handleChangeMonth(date!)}
    />
  );
};

export default MonthYearPicker;
